<?php require '../includes/functions.php';if(isset($_POST["brandlogo"])){$brandimage=$_FILES['image']['name'];$tmp_name=$_FILES['image']['tmp_name'];$imagesRand=rand().".jpg";move_uploaded_file($tmp_name,"uploads/brand/".$imagesRand);if($brandimage){$query=mysqli_query($conn,"INSERT INTO brandlogo(brandimg)VALUES('$imagesRand')");}if($query){exit(json_encode(["response"=>["code"=>"1","msg"=>"Your brand logo added successfully"]]));}}/* Delete Brand Logo*/if(isset($_POST["deleteBrandlogo"])){$id=$_POST["deleteBrandlogo"];$getdeletequery=mysqli_query($conn,"DELETE FROM brandlogo WHERE id=$id");if($getdeletequery) {exit(json_encode(array("response" => array("code" => "1" ,"msg" => "Brand Logo deleted Successfully"))));}else{exit(json_encode(array("response" => array("code" => "0" ,"msg" => "Something Went Wrong! Please try Again Later"))));}}/* Change Status*/if (isset($_POST["changeStatus"])) {$id=$_POST["changeStatus"];$queryGetStatus=mysqli_query($conn,"SELECT * FROM brandlogo WHERE id='$id'");$GetStatus=mysqli_fetch_array($queryGetStatus);if ($GetStatus["status"]=="0") {$status='1';}if ($GetStatus["status"]=='1') {$status='0';}$queryForStatusChange=mysqli_query($conn,"UPDATE brandlogo SET status='$status' WHERE id='$id'");if ($queryForStatusChange) {exit(json_encode(["response" => ["code" => "1","status" => $status]]));}else{exit(json_encode(["response" => ["code" => "0","status" => $status]]));}}secureAdmin();?><!DOCTYPE html><html lang="en">    <head>        <?php include 'partials/_style.php'; ?>    </head>    <!-- END HEAD -->    <body class="page-header-fixed sidemenu-closed-hidelogo page-content-white page-md header-white dark-sidebar-color logo-dark">        <div class="page-wrapper">            <!-- start header -->            <?php include 'partials/_header.php'; ?>            <!-- end header -->            <!-- start page container -->            <div class="page-container">                <!-- st