nd footer --></div><?php include 'partials/_script.php'; ?><!-- image preview when logo is uploaded --><script type="text/javascript">function image(img) {var src = img.src;window.open(src);}$("#addingBrandlogo").trigger('reset');</script><script type="text/javascript">$("#img_pre").click(function() {$("#userImage").click();});$("#addingBrandlogo").trigger('reset');</script><!-- add brand logo admin --><script type="text/javascript">function opendialog(){$("#imagebox").trigger('click');}$(document).ready(function (e) {$('#addingBrandlogo').on('submit',(function(e) {e.preventDefault();var formData = new FormData(this);$.ajax({type:'POST',url: 'brandlogo.php',data:formData,cache:false,dataType:'json',contentType: false,processData: false,success:function(data){if (data.response.code=='1') {$("#addingBrandlogo").trigger('reset');swal("success",data.response.msg,"success");setTimeout(function(){window.location="brandlogo.php";}, 2000);}if (data.response.code=='0') {swal("oh snap",data.response.msg,"warning");}},error: function(data){console.log("error");console.log(data);}});}));});</script><!--delete brand logo--><script type="text/javascript">$(".delete-brandlogo").click(function() {$(this).html("<i class='fa fa-ellipsis-h'></i>");var id=$(this).data("id");swal({title: "Are you sure?",text: "This will delete From Database And You'll Not be Able to recover it",icon: "warning",buttons: true,dangerMode: true,}).then((willDelete) => {if (willDelete) {$.ajax({url: 'brandlogo.php',type: 'POST',dataType:'json',data: {deleteBrandlogo: id},}).always(function(data) {console.log(data.response.msg);$(".row"+id).fadeOut('slow');$(this).html("<i class='fa fa-trash' ></i>");});}else {swal("Your Brand Logo is Safe!");$(this).html("<i class='fa fa-trash'></i>");}});});</script><!-- change status --><script type="text/javascript">$(".status").click(function() {var id=$(this).data("id");$.ajax({url: 'brandlogo.php',type: 'POST',dataType: 'json',data: {changeStatus: id},}).done(function(data) {if (data.response.code=='1') {if (data.response.status=='1') {$("#status"+id).removeClass('btn-danger');$("#status"+id).addClass('btn-success');$("#status"+id).html("Active");}if (data.response.status=='0') {$("#status"+id).removeClass('btn-success');$("#status"+id).addClass('btn-danger');$("#status"+id).html("Deactive");}}if (data.response.code=='0') {swal("oh Snap","Something Went Wrong Please Contact Developers!","error");}}).fail(function() {swal("oh Snap","Something Went Wrong Please Contact Developers!","error");}).always(function(data) {console.log(data);});});</script></body></html>