ol-sm-10">                    <input type="text" name="slug" value="<?php echo $fetchquerytag["slug"];?>" class="form-control" id="horizontalFormEmail" required="" placeholder="Tag Slug">                  </div>                </div>                <div class="form-group row">                  <label for="horizontalFormPassword" class="col-sm-2 control-label">Description</label>                  <div class="col-sm-10">                    <textarea class="form-control" required="" id="  horizontalFormPassword" name="description" placeholder="Description"><?php echo $fetchquerytag["description"];?></textarea>                  </div>                </div>                                <div class="form-group">                  <div class="offset-md-3 col-md-9">                    <button  type="submit" name="sub" class="btn btn-info">Submit</button>                    <button type="button" class="btn btn-default">Cancel</button>                  </div>                </div>              </form>            </div>          </div>        </div>      </div>      <!-- end page content -->    </div>  </div>  <!-- end page container -->  <!-- start footer -->  <?php include 'partials/_footer.php'; ?>  <!-- end footer --></div><?php include 'partials/_script.php'; ?>