='0') {						$("#status"+id).removeClass('bg-success');						$("#status"+id).addClass('bg-danger');						$("#status"+id).html("<i class='fa fa-thumbs-down'></i>");						$("#status"+id).attr("data-original-title","Deactive");					}				}			})			.fail(function() {				console.log("error");			})			.always(function(data) {				console.log(data);			});					});	</script></body></html>