isc = $('#ProductDiscount').val();                            var sprice = $('#offdiscount').val();                                                        if (disc != '' && amd != '' && sprice !='') {                            $('#offdiscount').val((parseInt(amd)) * (parseInt(disc))/100);                            $('#sellingPrice').val((parseInt(amd)) - (parseInt(sprice)));                            }else{                            $('#offdiscount').val(parseInt(amd));                            $('#sellingPrice').val(parseInt(disc));                                                        }                            });                            $(".getcategory").click(function() {                            var val=$(this).data("val");                            var id=$(this).data("id");                            $(".inputcateval").val(val);                            $(".inputcateid").val(id);                            });                            $(".getType").click(function() {                            var val=$(this).data("val");                            var id=$(this).data("id");                            $(".getProductVal").val(val);                            $(".getProductId").val(id);                            });                            <?php                            if (isset($_SESSION["success"])) { ?>                                                        swal("Success","Product Added Successfully!","success");                                                        <?php } unset($_SESSION["success"]);                            ?>                                                        //add product                            $(document).ready(function(event) {                            $("#addProductForm").on('submit', function(event) {                            event.preventDefault();                            $("#issubmited").val('1');                            });                            });                            Dropzone.options.myDropzone = {                            // Prevents Dropzone from uploading dropped files immediately                            autoProcessQueue: false,                            init: function() {                            var submitButton = document.querySelector("#submit-all");                            myDropzone = this; // closure                            submitButton.addEventListener("click", function() {                            $('#main').trigger('click');                            var val=$("#issubmited").val();                            if (val=='1') {                            myDropzone.processQueue();                            }                            // Tell Dropzone to process all queued files.                            });                            // You might want to show the submit button only when                            // files are dropped here:                            this.on("addedfile", function() {                            // Show submit button here and/or inform user to click it.                            // $("#submit-all").show();                            });                            myDropzone.on('sending', function(file, xhr, formData){                            var productName=$("#productName").val();                            var ProductVendor=$("#ProductVendor").val();                            var getProductVal=$(".getProductVal").val();                            var category=$(".category").val();                            var productPrice=$("#productPrice").val();                            var ProductDiscount=$("#ProductDiscount").val();                            var offdiscount=$("#offdiscount").val();                            var sellingPrice=$("#sellingPrice").val();                            var ProductQuantity=$("#ProductQuantity").val();                            var ProductColor=$("#ProductColor").val();                            var ProductSize=$("#ProductSize").val();                            var ProductsBrief=$("#ProductsBrief").val();                            formData.append('productName', productName);                            formData.append('ProductVendor', ProductVendor);                            formData.append('getProductVal', getProductVal);                            formData.append('category', category);                            formData.append('productPrice', productPrice);                            formData.append('ProductDiscount', ProductDiscount);                            formData.append('offdiscount', offdiscount);                            formData.append('sellingPrice', sellingPrice);                            formData.append('ProductQuantity', ProductQuantity);                            formData.append('ProductColor', ProductColor);                            formData.append('ProductSize', ProductSize);                            formData.append('ProductsBrief', ProductsBrief);                                                        });                            myDropzone.on('success', function( file, resp ){                            window.location='add-product.php';                                                        });                            }                            };                            </script>                        </body>                    </html>