text: "Once deleted, you will not be able to recover this Variant!",				icon: "warning",				buttons: true,				dangerMode: true,				})					.then((willDelete) => {						if (willDelete) {						$.ajax({								type:'POST',								url: 'view-product-info.php',								data:{token:token},								dataType:'json',								success:function(data){									console.log(data);									if(data.response.code=='1')									{										swal("Done",data.response.msg,"success");										$(".tr"+token).hide('slow');									}									if(data.response.code=='0')									{										swal("Opps!",data.response.msg,"error");										}								},								error: function(data){									console.log("error");									console.log(data);								}							});						}						else {						swal("Your Variant file is safe!");						}					});					});					});		$(".status").click(function() {			/* Act on the event */			var id=$(this).val();			$.ajax({				url: 'view-product-info.php',				type: 'POST',				dataType: 'json',				data: {changeStatus: id},			})			.done(function() {				console.log("success");			})			.fail(function() {				console.log("error");			})			.always(function(data) {				console.log(data);			});					});</script></body></html>