willDelete) => {  if (willDelete) {  $.ajax({  url: 'ajax/delete_vendor.php',  type: 'POST',  data: {id: id},  })  .done(function(data) {  if (data=='1') {  $(".er"+id).hide('slow');  swal("Success","Product Deleted Successfully","success");  }  });    } else {  swal("Your Product safe!");  }  });  });  </script>  </body></html>